var p1Text=['{"f":"我",  "t": "都七点多了……终于做完了，可以吃晚饭去了。"}','{"f":"老板",  "t": "你过来下，我有点事情想跟你谈谈"}','{"f":"我",  "t": "……好的（可恶，不知道什么时候才能吃饭）。"}'],p2Text=['{"f":"老板",  "t": "你这个报告做的跟一坨屎有什么区别"}','{"f":"我",  "t": "(你自己做的屎都不如！)"}','{"f":"老板",  "t": "你这种人做一辈子也只能在底层！"}','{"f":"我",  "t": "(因为高层都被你这种人渣占着呢。)"}','{"f":"老板",  "t": "养着你都是浪费公司的钱，这个月扣你两千。"}','{"f":"我",  "t": "(卧槽，侮辱我就算了还想扣我钱，掀桌！)"}'];